mapper.insert:
insert into member(
		id id
    ,pwd pwd
    ,email email
    ,picture picture
    ,enabled enabled
    ,regDate regDate
    ,phone phone
    ,name name
    ,register register
    ,address address
    ,authority authority
)values(
  #{id}
  ,#{pwd}
  ,#{email}
  ,#{picture}
  ,#{enabled}
  ,#{regDate}
  ,#{phone}
  ,#{name}
  ,#{register}
  ,#{address}
  ,#{authority}
)

mapper.update:
update member set
  	id = #{id}
    ,pwd = #{pwd}
    ,email = #{email}
    ,picture = #{picture}
    ,enabled = #{enabled}
    ,regDate = #{regDate}
    ,phone = #{phone}
    ,name = #{name}
    ,register = #{register}
    ,address = #{address}
    ,authority = #{authority}
where id = #{id}
